MSESControl.OnClick:
var OrgType = MCgetAttribute('data/Declarationsrecords/Declarations/@ConsigneeType',null);
var OrganizationId = MCgetAttribute('data/Declarationsrecords/Declarations/@ConsigneeId',null);

if (OrgType =='o')
{
	LockupScreen('','ViewOrganizationPopUp','ViewOrganization',ProfileField('Organizations.OrganizationId','text') + '=N\''+ OrganizationId +'\'',750,350,true,false);return false;
}
else
{
	LockupScreen('','ViewEmployeePopUp','ViewEmployee',ProfileField('People.PersonalId','text') + '=N\''+ OrganizationId +'\'',750,350,true,false);return false;
}

var DecId = MCgetAttribute('data/Declarationsrecords/Declarations/@DeclarationId','');
var HBId = MCgetAttribute('data/Declarationsrecords/Declarations/@HouseBillId','');
