var criteria = DecId  + ',' + HBId + ','+ ManifestId+',InspectionRequestFromDeclaration';

LockupScreen('','InspectionRequestPLsPg','SearchInspections',criteria,700,370,true,false);

//GetSelectedNodesScreen(this.form.name,'ListUnAssociatedCommercialInvoice','ListUnAssociatedCommercialInvoice','',700,400,true,false,'CommercialInvoices','DeclarationId',MCgetAttribute('data/Declarationsrecords/Declarations/@DeclarationId',null),'data.Declarationsrecords.Declarations.CommercialInvoicesrecords',Array('CommercialInvoiceId'));
