	}
else if (checkRefDecId() == 'true')
{
	var criteria = ProfileField('Declarations.DeclarationId') + '=\'' + MCgetAttribute('data/Declarationsrecords/Declarations/@RefDeclarationId','')+ '\'';
	}

GetSelectedNodesScreen(
